INSERT INTO movie_awards(id,movie_year,category,nominee,additional_info,won) values (UUID(),'2005','Scientific and Technical (Bonner Medal)','To Don Hall in appreciation for outstanding service and dedication in upholding the high standards of the Academy of Motion Picture Arts and Sciences.',NULL,'YES');
